import React from 'react';

interface InputNodeProps {
  data: {
    label: string;
    inputText: string; // Add inputText prop to capture user input
    setInputText: (text: string) => void; // Function to update inputText
  };
}

export const InputNode: React.FC<InputNodeProps> = ({ data }) => {
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    data.setInputText(e.target.value); // Call the function to update inputText
  };

  return (
    <div style={{ padding: '10px', background: '#D8F3DC', border: '1px solid #40916C', borderRadius: '5px' }}>
      <strong>{data.label}</strong>
      <div>
        <input type="text" placeholder="Enter Input" value={data.inputText} onChange={handleChange} />
      </div>
    </div>
  );
};
